void	print_stacks(int **stacks, int *end)
{
	int i;

	printf("\nSTACK A:  ");
	i = -1;
	while (++i < end[0])
	{
		printf("%d ", stacks[0][i]);
	}
	printf("\nSTACK B:  ");
	i = -1;
	while (++i < end[1])
	{
		printf("%d ", stacks[1][i]);
	}
}

